import React, { useEffect, useRef } from 'react';
import ChatBubble from './ChatBubble';
import Character from './Character';
import BottomNavigation from './BottomNavigation';
import './AstroBotUI.css';
import Astro_Background from '../assets/Astro_Background.mp4';

const AstroBotUI = () => {
  const videoRef = useRef(null);

  // Speed up background video playback for a livelier feel
  useEffect(() => {
    const v = videoRef.current;
    if (!v) return;
    const applyRate = () => {
      try { v.playbackRate = 0.85; } catch (e) {}
    };
    // Seamless-ish loop: jump slightly before end to avoid decoder pause
    const smoothLoop = () => {
      if (!v.duration || isNaN(v.duration)) return;
      const threshold = 0.15; // seconds before end to reset
      if (v.currentTime >= v.duration - threshold) {
        try {
          v.currentTime = 0.01;
          // ensure it keeps playing after seek
          const p = v.play();
          if (p && typeof p.catch === 'function') p.catch(() => {});
        } catch (e) {}
      }
    };
    applyRate();
    v.addEventListener('loadedmetadata', applyRate);
    v.addEventListener('loadeddata', applyRate);
    v.addEventListener('timeupdate', smoothLoop);
    return () => {
      v.removeEventListener('loadedmetadata', applyRate);
      v.removeEventListener('loadeddata', applyRate);
      v.removeEventListener('timeupdate', smoothLoop);
    };
  }, []);
  return (
    <div className="astrobot-container">
      {/* Background Video Layer */}
      <div className="background-layer">
        <video
          ref={videoRef}
          className="background-video"
          src={Astro_Background}
          autoPlay
          muted
          loop
          playsInline
          preload="auto"
        //   poster="/Astro_Backgorund.png"
         />
        <div className="background-overlay" />
      </div>

      {/* Black Header with Logo (top-left) */}
      <div className="top-header">
        <img src="/Astro_LOGO.png" alt="AstroBot Logo" className="header-logo" />
      </div>

      {/* Central Section with Character and Chat */}
      <div className="central-section">
        <Character />
        <ChatBubble />
      </div>

      {/* Bottom Navigation */}
      <BottomNavigation />
    </div>
  );
};

export default AstroBotUI;
