import React from 'react';
import ChatBubble from './ChatBubble';
import Character from './Character';
import BottomNavigation from './BottomNavigation';
import './AstroBotUI.css';

const AstroBotUI = () => {
  return (
    <div className="astrobot-container">
      {/* Background Video Layer */}
      <div className="background-layer">
        <video
          className="background-video"
          src="/Astro_Background.mp4"
          autoPlay
          muted
          loop
          playsInline
          preload="auto"
          poster="/Astro_Backgorund.png"
        />
        <div className="background-overlay" />
      </div>

      {/* Black Header with Logo (top-left) */}
      <div className="top-header">
        <img src="/Astro_LOGO.png" alt="AstroBot Logo" className="header-logo" />
      </div>

      {/* Central Section with Character and Chat */}
      <div className="central-section">
        <Character />
        <ChatBubble />
      </div>

      {/* Bottom Navigation */}
      <BottomNavigation />
    </div>
  );
};

export default AstroBotUI;
