import React, { useState } from 'react';
import './ExpandableChat.css';
import astroBotAPI from '../services/api';

const ExpandableChat = ({ isOpen, onClose, onRefresh }) => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Namaste! ðŸ™ Main Pandit ji hun, ek anubhavi astrologer. Aapka swagat hai AstroRemedis mein!",
      sender: 'pandit',
      timestamp: new Date().toLocaleTimeString()
    },
    {
      id: 2,
      text: "Aapka naam kya hai aur aapki janm tithi, samay aur sthan batayiye taaki main aapka Kundli banakar detailed analysis kar sakun.",
      sender: 'pandit',
      timestamp: new Date().toLocaleTimeString()
    }
  ]);
  const [inputText, setInputText] = useState('');
  const [userProfile, setUserProfile] = useState({
    name: '',
    dob: '',
    tob: '',
    place: '',
    timezone: 'Asia/Kolkata'
  });
  const [kundliData, setKundliData] = useState(null);
  const [isGeneratingKundli, setIsGeneratingKundli] = useState(false);
  const [currentStep, setCurrentStep] = useState('greeting'); // greeting, collecting_details, kundli_generated, chatting

  // Handle refresh - clear messages and reset to initial state
  React.useEffect(() => {
    if (onRefresh) {
      const resetMessages = () => {
        setMessages([
          {
            id: 1,
            text: "Namaste! ðŸ™ Main Pandit ji hun, ek anubhavi astrologer. Aapka swagat hai AstroRemedis mein!",
            sender: 'pandit',
            timestamp: new Date().toLocaleTimeString()
          },
          {
            id: 2,
            text: "Aapka naam kya hai aur aapki janm tithi, samay aur sthan batayiye taaki main aapka Kundli banakar detailed analysis kar sakun.",
            sender: 'pandit',
            timestamp: new Date().toLocaleTimeString()
          }
        ]);
        setInputText('');
        setUserProfile({
          name: '',
          dob: '',
          tob: '',
          place: '',
          timezone: 'Asia/Kolkata'
        });
        setKundliData(null);
        setCurrentStep('greeting');
      };

      // Listen for refresh events
      const handleRefresh = () => {
        resetMessages();
      };

      // Store the handler so we can clean it up
      window.addEventListener('refreshChat', handleRefresh);

      return () => {
        window.removeEventListener('refreshChat', handleRefresh);
      };
    }
  }, [onRefresh]);

  // Helper function to extract birth details from user input
  const extractBirthDetails = (text) => {
    const details = {};

    // Extract name (if not already set)
    if (!userProfile.name) {
      const nameMatch = text.match(/(?:m[ae]ra naam|my name is|i am|main|mera naam)\s+([a-zA-Z\s]+)/i);
      if (nameMatch) {
        details.name = nameMatch[1].trim();
      }
    }

    // Extract date of birth
    const dobMatch = text.match(/(\d{1,2})[\/\-](\d{1,2})[\/\-](\d{4})/);
    if (dobMatch) {
      const [, day, month, year] = dobMatch;
      details.dob = `${year}-${month.padStart(2, '0')}-${day.padStart(2, '0')}`;
    }

    // Extract time of birth
    const tobMatch = text.match(/(\d{1,2}):(\d{2})(?:\s*(am|pm))?/i);
    if (tobMatch) {
      let [, hour, minute, period] = tobMatch;
      hour = parseInt(hour);
      if (period && period.toLowerCase() === 'pm' && hour !== 12) {
        hour += 12;
      } else if (period && period.toLowerCase() === 'am' && hour === 12) {
        hour = 0;
      }
      details.tob = `${hour.toString().padStart(2, '0')}:${minute}:00`;
    }

    // Extract place
    const placeMatch = text.match(/(?:place|sthan|city|town|birth place|janm sthan)\s*:?\s*([a-zA-Z\s,]+)/i);
    if (placeMatch) {
      details.place = placeMatch[1].trim();
    }

    return details;
  };

  // Helper function to check if all birth details are collected
  const isProfileComplete = () => {
    return userProfile.name && userProfile.dob && userProfile.tob && userProfile.place;
  };

  // Helper function to generate Kundli
  const generateKundli = async () => {
    setIsGeneratingKundli(true);

    try {
      const response = await astroBotAPI.generateKundli({
        name: userProfile.name,
        dob: userProfile.dob,
        tob: userProfile.tob,
        place: userProfile.place,
        timezone: userProfile.timezone
      });

      if (response.success) {
        setKundliData(response.chart_data);
        setCurrentStep('kundli_generated');

        // Add Kundli generation success message
        const successMessage = {
          id: messages.length + 2,
          text: `ðŸŽ‰ ${userProfile.name} ji, aapka Kundli successfully generate ho gaya hai! Main ab aapke liye detailed analysis kar sakta hun. Aap kya jaanna chahte hain?`,
          sender: 'pandit',
          timestamp: new Date().toLocaleTimeString()
        };
        setMessages(prev => [...prev, successMessage]);
      } else {
        throw new Error(response.error || 'Kundli generation failed');
      }
    } catch (error) {
      console.error('Error generating Kundli:', error);
      const errorMessage = {
        id: messages.length + 2,
        text: "Sorry, Kundli generate karne mein koi problem aa rahi hai. Kripya dobara try karein ya contact karein.",
        sender: 'pandit',
        timestamp: new Date().toLocaleTimeString()
      };
      setMessages(prev => [...prev, errorMessage]);
    } finally {
      setIsGeneratingKundli(false);
    }
  };

  const handleSendMessage = async () => {
    if (inputText.trim()) {
      const newMessage = {
        id: messages.length + 1,
        text: inputText,
        sender: 'user',
        timestamp: new Date().toLocaleTimeString()
      };

      setMessages([...messages, newMessage]);
      const currentInput = inputText;
      setInputText('');

      try {
        // Show typing indicator
        const typingMessage = {
          id: messages.length + 2,
          text: "Pandit ji typing...",
          sender: 'pandit',
          timestamp: new Date().toLocaleTimeString(),
          isTyping: true
        };
        setMessages(prev => [...prev, typingMessage]);

        // Extract birth details from user input
        const extractedDetails = extractBirthDetails(currentInput);
        const updatedProfile = { ...userProfile, ...extractedDetails };
        setUserProfile(updatedProfile);

        let response;

        if (currentStep === 'greeting' || currentStep === 'collecting_details') {
          // Check if we have all required details
          if (isProfileComplete()) {
            // Generate Kundli
            await generateKundli();
            return; // generateKundli handles the response
          } else {
            // Ask for missing details
            const missingDetails = [];
            if (!updatedProfile.name) missingDetails.push('naam');
            if (!updatedProfile.dob) missingDetails.push('janm tithi (DD/MM/YYYY)');
            if (!updatedProfile.tob) missingDetails.push('janm samay (HH:MM)');
            if (!updatedProfile.place) missingDetails.push('janm sthan');

            response = {
              response: `Aapka naam ${updatedProfile.name || 'batayiye'}, janm tithi ${updatedProfile.dob || 'batayiye'}, janm samay ${updatedProfile.tob || 'batayiye'}, aur janm sthan ${updatedProfile.place || 'batayiye'}. In sab details ke saath main aapka Kundli banakar detailed analysis kar sakta hun.`
            };
            setCurrentStep('collecting_details');
          }
        } else {
          // Regular chat with Kundli context
          response = await astroBotAPI.sendChatMessage(currentInput, kundliData);
        }

        // Remove typing indicator and add actual response
        setMessages(prev => {
          const withoutTyping = prev.filter(msg => !msg.isTyping);
          const panditResponse = {
            id: messages.length + 2,
            text: response.response,
            sender: 'pandit',
            timestamp: new Date().toLocaleTimeString()
          };
          return [...withoutTyping, panditResponse];
        });

      } catch (error) {
        console.error('Error sending message:', error);

        // Remove typing indicator and show error
        setMessages(prev => {
          const withoutTyping = prev.filter(msg => !msg.isTyping);
          const errorResponse = {
            id: messages.length + 2,
            text: "Sorry, main abhi online nahi hun. Kripya thoda baad try karein.",
            sender: 'pandit',
            timestamp: new Date().toLocaleTimeString()
          };
          return [...withoutTyping, errorResponse];
        });
      }
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  if (!isOpen) return null;

  return (
    <div className={`expandable-chat-container ${isOpen ? 'expanded' : ''}`}>
      {/* Chat Header */}
      <div className="chat-header">
        <div className="pandit-info">
          <div className="pandit-avatar-small">
            <img src={require('../assets/Astro_Avatar.png')} alt="Pandit ji" />
          </div>
          <div className="pandit-details">
            <h3>Pandit ji</h3>
            <span className="status">Online</span>
          </div>
        </div>
      </div>

      {/* Messages Area */}
      <div className="messages-container">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.sender}`}>
            {message.sender === 'pandit' && (
              <div className="message-avatar">
                <img src={require('../assets/Astro_Avatar.png')} alt="Pandit ji" />
              </div>
            )}
            <div className="message-content">
              <div className="message-bubble">
                <p>{message.text}</p>
                <span className="message-time">{message.timestamp}</span>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Input Area */}
      <div className="input-container">
        <div className="input-wrapper">
          <input
            type="text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Apna sawal yahan likhein..."
            className="message-input"
          />
          <button onClick={handleSendMessage} className="send-button">
            <svg viewBox="0 0 24 24" className="send-icon">
              <path d="M22 2L11 13" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M22 2L15 22L11 13L2 9L22 2Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ExpandableChat;
