import React, { useState } from 'react';
import './ExpandableChat.css';

const ExpandableChat = ({ isOpen, onClose, onRefresh }) => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Namaste! 🙏 Main Pandit ji hun. Aapka swagat hai AstroRemedis mein!",
      sender: 'pandit',
      timestamp: new Date().toLocaleTimeString()
    }
  ]);
  const [inputText, setInputText] = useState('');

  // Handle refresh - clear messages and reset to initial state
  React.useEffect(() => {
    if (onRefresh) {
      const resetMessages = () => {
        setMessages([
          {
            id: 1,
            text: "Namaste! 🙏 Main Pandit ji hun. Aapka swagat hai AstroRemedis mein!",
            sender: 'pandit',
            timestamp: new Date().toLocaleTimeString()
          }
        ]);
        setInputText('');
      };

      // Listen for refresh events
      const handleRefresh = () => {
        resetMessages();
      };

      // Store the handler so we can clean it up
      window.addEventListener('refreshChat', handleRefresh);

      return () => {
        window.removeEventListener('refreshChat', handleRefresh);
      };
    }
  }, [onRefresh]);

  const handleSendMessage = () => {
    if (inputText.trim()) {
      const newMessage = {
        id: messages.length + 1,
        text: inputText,
        sender: 'user',
        timestamp: new Date().toLocaleTimeString()
      };

      setMessages([...messages, newMessage]);
      setInputText('');

      // Simulate Pandit ji's response
      setTimeout(() => {
        const panditResponse = {
          id: messages.length + 2,
          text: "Aapka sawal bahut achha hai! Main aapke liye detailed analysis karunga. Kripya thoda sa wait karein...",
          sender: 'pandit',
          timestamp: new Date().toLocaleTimeString()
        };
        setMessages(prev => [...prev, panditResponse]);
      }, 1500);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  if (!isOpen) return null;

  return (
    <div className={`expandable-chat-container ${isOpen ? 'expanded' : ''}`}>
      {/* Chat Header */}
      <div className="chat-header">
        <div className="pandit-info">
          <div className="pandit-avatar-small">
            <img src={require('../assets/Astro_Avatar.png')} alt="Pandit ji" />
          </div>
          <div className="pandit-details">
            <h3>Pandit ji</h3>
            <span className="status">Online</span>
          </div>
        </div>
      </div>

      {/* Messages Area */}
      <div className="messages-container">
        {messages.map((message) => (
          <div key={message.id} className={`message ${message.sender}`}>
            {message.sender === 'pandit' && (
              <div className="message-avatar">
                <img src={require('../assets/Astro_Avatar.png')} alt="Pandit ji" />
              </div>
            )}
            <div className="message-content">
              <div className="message-bubble">
                <p>{message.text}</p>
                <span className="message-time">{message.timestamp}</span>
              </div>
            </div>
          </div>
        ))}
      </div>

      {/* Input Area */}
      <div className="input-container">
        <div className="input-wrapper">
          <input
            type="text"
            value={inputText}
            onChange={(e) => setInputText(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Apna sawal yahan likhein..."
            className="message-input"
          />
          <button onClick={handleSendMessage} className="send-button">
            <svg viewBox="0 0 24 24" className="send-icon">
              <path d="M22 2L11 13" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              <path d="M22 2L15 22L11 13L2 9L22 2Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </button>
        </div>
      </div>
    </div>
  );
};

export default ExpandableChat;
