import React, { useState } from 'react';
import './ChatWindow.css';

const ChatWindow = ({ isOpen, onClose }) => {
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Namaste 🙏 AstroRemedis mein aapka swagat hai! Main aapki kya madad kar sakta hun?",
      isUser: false,
      timestamp: new Date()
    }
  ]);
  const [inputText, setInputText] = useState('');

  const handleSendMessage = () => {
    if (inputText.trim()) {
      const newMessage = {
        id: messages.length + 1,
        text: inputText,
        isUser: true,
        timestamp: new Date()
      };
      
      setMessages([...messages, newMessage]);
      setInputText('');
      
      // Simulate Pandit ji response (replace with actual API call)
      setTimeout(() => {
        const response = {
          id: messages.length + 2,
          text: "Aapka sawal bahut achha hai. Main aapko detailed analysis de sakta hun. Kripya thoda samay dijiye.",
          isUser: false,
          timestamp: new Date()
        };
        setMessages(prev => [...prev, response]);
      }, 1000);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  if (!isOpen) return null;

  return (
    <div className="chat-window-overlay" onClick={onClose}>
      <div className="chat-window" onClick={(e) => e.stopPropagation()}>
        {/* Chat Header */}
        <div className="chat-header">
          <div className="chat-header-info">
            <div className="pandit-avatar-small">
              <img src={require('../assets/Astro_Avatar.png')} alt="Pandit ji" />
            </div>
            <div className="chat-header-text">
              <h3>Pandit ji</h3>
              <span className="status">Online</span>
            </div>
          </div>
          <button className="close-chat-btn" onClick={onClose}>
            <svg viewBox="0 0 24 24" width="20" height="20">
              <path d="M18 6L6 18" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
              <path d="M6 6l12 12" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
            </svg>
          </button>
        </div>

        {/* Messages Area */}
        <div className="messages-container">
          {messages.map((message) => (
            <div key={message.id} className={`message ${message.isUser ? 'user-message' : 'pandit-message'}`}>
              {!message.isUser && (
                <div className="message-avatar">
                  <img src={require('../assets/Astro_Avatar.png')} alt="Pandit ji" />
                </div>
              )}
              <div className="message-content">
                <div className="message-bubble">
                  <p>{message.text}</p>
                </div>
                <span className="message-time">
                  {message.timestamp.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                </span>
              </div>
            </div>
          ))}
        </div>

        {/* Input Area */}
        <div className="chat-input-area">
          <div className="input-container">
            <input
              type="text"
              value={inputText}
              onChange={(e) => setInputText(e.target.value)}
              onKeyPress={handleKeyPress}
              placeholder="Apna sawal likhiye..."
              className="chat-input"
            />
            <button onClick={handleSendMessage} className="send-btn">
              <svg viewBox="0 0 24 24" width="20" height="20">
                <path d="M22 2L11 13" stroke="currentColor" strokeWidth="2" strokeLinecap="round"/>
                <path d="M22 2L15 22L11 13L2 9L22 2Z" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ChatWindow;
