import React, { useState } from 'react';
import ChatBubble from './ChatBubble';
import Character from './Character';
import BottomNavigation from './BottomNavigation';
import ExpandableChat from './ExpandableChat';
import './AstroBotUI.css';
// No video import; using procedural CSS background

const AstroBotUI = () => {
  const [isChatOpen, setIsChatOpen] = useState(false);

  const handleChatToggle = () => {
    setIsChatOpen(!isChatOpen);
  };

  const handleCloseChat = () => {
    setIsChatOpen(false);
  };

  const handleRefreshChat = () => {
    // Trigger refresh event for ExpandableChat
    window.dispatchEvent(new CustomEvent('refreshChat'));
    console.log('Refreshing chat - clearing messages');
  };

  return (
    <div className="astrobot-container">
      {/* Background (static image with gentle pan/zoom) */}
      <div className="background-layer" />

      {/* Black Header with Logo (top-left) */}
      <div className="top-header">
        <img src="/Astro_LOGO.png" alt="AstroBot Logo" className="header-logo" />
      </div>

      {/* Central Section with Character and Chat */}
      <div className="central-section">
        {!isChatOpen && <Character />}
        {!isChatOpen && <ChatBubble />}
      </div>

      {/* Bottom Navigation */}
      <BottomNavigation onChatToggle={handleChatToggle} isChatOpen={isChatOpen} onRefreshChat={handleRefreshChat} />

      {/* Expandable Chat Window */}
      <ExpandableChat isOpen={isChatOpen} onClose={handleCloseChat} onRefresh={handleRefreshChat} />
    </div>
  );
};

export default AstroBotUI;
