import React from 'react';
import './KundliChart.css';

const KundliChart = ({ chartData, onChartReady, compact = false }) => {
  const { svg_content, format, chart_type, ayanamsa, astrology_system } = chartData || {};

  React.useEffect(() => {
    if (svg_content && onChartReady) {
      onChartReady();
    }
  }, [svg_content, onChartReady]);

  // Post-process injected SVG to prevent overlapping labels
  React.useEffect(() => {
    if (!svg_content) return;
    // Allow the DOM to paint the SVG first
    const timer = setTimeout(() => {
      try {
        const root = document.querySelector('.svg-chart svg');
        if (!root) return;

        // 1) Normalize multi-line spacing if <tspan> are used
        Array.from(root.querySelectorAll('text')).forEach((t) => {
          const tspans = Array.from(t.querySelectorAll('tspan'));
          if (tspans.length > 1) {
            tspans.forEach((sp, idx) => {
              sp.setAttribute('dy', idx === 0 ? '0' : '1.1em');
              sp.setAttribute('x', t.getAttribute('x') || '0');
            });
          }
        });

        // 2) Cluster nearby text elements (same X column) and spread them
        const texts = Array.from(root.querySelectorAll('text'))
          .map(el => ({
            el,
            x: parseFloat(el.getAttribute('x') || '0'),
            y: parseFloat(el.getAttribute('y') || '0'),
            fs: parseFloat(el.getAttribute('font-size') || '14')
          }))
          .filter(t => !Number.isNaN(t.x) && !Number.isNaN(t.y));

        // Sort by x then y for stable clustering
        texts.sort((a, b) => (a.x - b.x) || (a.y - b.y));

        const visited = new Set();
        const minGap = 25; // px gap between stacked labels
        const xThreshold = 18; // consider same column if x is within 18px
        const yThreshold = 40; // cluster window in y

        for (let i = 0; i < texts.length; i++) {
          if (visited.has(texts[i].el)) continue;
          const cluster = [texts[i]];
          visited.add(texts[i].el);

          for (let j = i + 1; j < texts.length; j++) {
            if (visited.has(texts[j].el)) continue;
            if (Math.abs(texts[j].x - texts[i].x) <= xThreshold && Math.abs(texts[j].y - texts[i].y) <= yThreshold) {
              cluster.push(texts[j]);
              visited.add(texts[j].el);
            }
          }

          if (cluster.length > 1) {
            // Spread vertically with minimum gap
            cluster.sort((a, b) => a.y - b.y);
            const baseY = cluster[0].y;
            cluster.forEach((t, idx) => {
              const newY = baseY + idx * minGap;
              t.el.setAttribute('y', String(newY));
              t.el.setAttribute('text-anchor', t.el.getAttribute('text-anchor') || 'middle');
              t.el.setAttribute('dominant-baseline', t.el.getAttribute('dominant-baseline') || 'middle');
            });
          }
        }
      } catch (e) {
        // fail-safe
      }
    }, 0);
    return () => clearTimeout(timer);
  }, [svg_content]);

  const renderSVGChart = () => {
    if (svg_content) {
      return (
        <div className="svg-chart-container">
          <div 
            className="svg-chart"
            dangerouslySetInnerHTML={{ __html: svg_content }}
          />
        </div>
      );
    }
    return (
      <div className="chart-placeholder">
        <div className="chart-loading">
          <div className="spinner"></div>
          <p>Generating your Kundli chart...</p>
        </div>
      </div>
    );
  };

  const renderChartInfo = () => {
    if (!astrology_system) return null;

    return (
      <div className="chart-info">
        <h3>🌟 Your Kundli Chart</h3>
        <div className="chart-details">
          <div className="detail-item">
            <span className="label">Astrology System:</span>
            <span className="value">{astrology_system} Astrology</span>
          </div>
          <div className="detail-item">
            <span className="label">Chart Style:</span>
            <span className="value">{chart_type?.replace('-', ' ').toUpperCase()}</span>
          </div>
          <div className="detail-item">
            <span className="label">Ayanamsa:</span>
            <span className="value">{ayanamsa} (KP System)</span>
          </div>
        </div>
      </div>
    );
  };

  if (compact) {
    return (
      <div className="kundli-chart-container compact">
        <div className="chart-wrapper compact">
          {renderSVGChart()}
        </div>
      </div>
    );
  }

  return (
    <div className="kundli-chart-container">
      {renderChartInfo()}
      <div className="chart-wrapper">
        {renderSVGChart()}
      </div>
      <div className="chart-instructions">
        <p>📋 <strong>Your Kundli chart is ready!</strong> You can now ask me any questions about your chart, planetary positions, or astrological predictions.</p>
      </div>
    </div>
  );
};

export default KundliChart;
