import React from 'react';
import './KundliChart.css';

const KundliChart = ({ chartData, onChartReady }) => {
  const { prokerala_data, chart_config } = chartData || {};
  const chart = prokerala_data?.chart || {};

  React.useEffect(() => {
    if (chart.svg_content && onChartReady) {
      onChartReady();
    }
  }, [chart.svg_content, onChartReady]);

  const renderSVGChart = () => {
    if (chart.svg_content) {
      return (
        <div className="svg-chart-container">
          <div 
            className="svg-chart"
            dangerouslySetInnerHTML={{ __html: chart.svg_content }}
          />
        </div>
      );
    }
    return (
      <div className="chart-placeholder">
        <div className="chart-loading">
          <div className="spinner"></div>
          <p>Generating your Kundli chart...</p>
        </div>
      </div>
    );
  };

  const renderChartInfo = () => {
    if (!chart_config) return null;
    
    return (
      <div className="chart-info">
        <h3>ðŸŒŸ Your Kundli Chart</h3>
        <div className="chart-details">
          <div className="detail-item">
            <span className="label">Astrology System:</span>
            <span className="value">{chart_config.astrology_system} Astrology</span>
          </div>
          <div className="detail-item">
            <span className="label">Chart Style:</span>
            <span className="value">{chart_config.chart_style?.replace('-', ' ').toUpperCase()}</span>
          </div>
          <div className="detail-item">
            <span className="label">Ayanamsa:</span>
            <span className="value">{chart_config.ayanamsa} (KP System)</span>
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="kundli-chart-container">
      {renderChartInfo()}
      <div className="chart-wrapper">
        {renderSVGChart()}
      </div>
      <div className="chart-instructions">
        <p>ðŸ“‹ <strong>Your Kundli chart is ready!</strong> You can now ask me any questions about your chart, planetary positions, or astrological predictions.</p>
      </div>
    </div>
  );
};

export default KundliChart;
